  //     },
  //     {
  //       path: '/permissions',
  //       name: 'permissions',
  //       meta: {index: 6},
  //       component: () => import(/* webpackChunkName: "set" */ '../views/set/Permissions.vue'),
  //     },
  //   ]
  // }
]

// 注册路由
const router = new VueRouter({
  routes: constantRouterMap
})

// 异步路由
export const asyncRouter = [
  {
    path: '/home',
    name: 'home',
    meta: { index: 1 },
    redirect: '/dashboardover',
    component: () => import(/* webpackChunkName: "home" */ '../views/home/Home.vue'),
    children: [
      {
        path: '/dashboardover',
        name: 'dashboardover',
        meta: { index: 2 },
        component: () => import(/* webpackChunkName: "dashboardover" */ '../views/dashboardover/Dashboardover.vue'),
      },
      {
        path: '/goodslist',
        name: 'goodslist',
        meta: { index: 3 },
        component: () => import(/* webpackChunkName: "goods" */ '../views/goods/GoodsList.vue'),
      },
      {
        path: '/orderlist',
        name: 'orderlist',
        meta: { index: 4 },
        component: () => import(/* webpackChunkName: "order" */ '../views/order/OrderList.vue'),
      },
      {
        path: '/customerlist',
        name: 'customerlist',
        meta: { index: 5 },
        component: () => import(/* webpackChunkName: "customer" */ '../views/customer/CustomerList.vue'),
      },
      {
        path: '/permissions',
        name: 'permissions',
        meta: { index: 6 },
        component: () => import(/* webpackChunkName: "set" */ '../views/set/Permissions.vue'),
      },
    ]
  }
]

// 处理component
function loadView(component) {
  return () => import(/* webpackChunkName: "home" */ `../views/${component}`)
  // return (resolve) => require([`@/views/${component}`], resolve)
}
// function formatRouter(routes, Layout) {
//   const formatRouterArr = []
//   routes.forEach(route => {

//   })
// }

// 根据后端返回权限匹配对应路由
function routerMatch(permission, asyncRoute) {
  return new Promise((resolve, reject) => {
    const routers = [
      {
        path: '/home',
        name: 'home',
        meta: { index: 1 },
        // redirect: '/dashboardover',
        component: () => import(/* webpackChunkName: "home" */ '../views/home/Home.vue'),
        children: []
      }
    ]
    function createRouter(permission) {
      permission.forEach(item => {
        console.log('后台返回', item)
        if (item.childs && item.childs.length > 0) {
          createRouter(item.childs)
        }
        let path = item.path
        asyncRoute.find(s => {
          if (s.children) {
            s.children.find(y => {
              console.log('前端保存', y)
              console.log(path)
              if (y.name === path) {
                if (item.component === 'Layout') {
                  y.component = loadView()
                } else {
                  y.component = loadView(item.component)
                  routers[0].children.push(y)
                }
              }
            })
          } else {
            if (s.name === path) {
              s.component = loadView(item.component)
              routers[0].children.push(s)
            }
          }
        })
      })
    }
    createRouter(permission)
    console.log('最终的路由数组', routers)

    resolve(routers)
  })
}

// 挂载路由守卫
router.beforeEach((to, from, next) => {
  if (to.path === '/login') return next()
  // 获取token
  let token = window.sessionStorage.getItem('token')
  const tokenStart = window.sessionStorage.getItem('tokenStart')
  const nowDate = new Date().getTime()
  const timeOver = 3600 * 24 * 1000
  if (nowDate - tokenStart > timeOver) {
    token = null
  }
  if (token) {
    if (to.path === '/login') {
      return next('/dashboardover')
    } else {
      const permiss = store.state.menuRouterList
      console.log('permiss', permiss)
      routerMatch(permiss, asyncRouter).then(res => {
        res[0].children.forEach(item => {
          router.addRoute('home', item)
        })
        next({ ...to, replace: true })
      }).catch((err) => {
        console.log(err)
        router.replace('/kk')
      })
      // routerMatch(permiss, asyncRouter).then(res => {
      //   console.log('合并后的路由列表', res)
      //   // let mergeRouter = res[0]

      //   console.log('合并后的路由列表2', res[0].component)
      //   const home = 'home/Home'
      //   res[0].component = loadView(home)
      //   console.log('合并后的路由列表2', res)
      //   res.forEach(item => {
      //     router.addRoute(item)
      //   })
      //   next(to.path)
      // }).catch((err) => {
      //   console.log(err)
      //   router.replace('/kk')
      // })

    }

  } else {
    Message.error('登录状态过期，请重新登录')
    window.sessionStorage.clear()
    return next('/login')
  }


  next()
})

export default router
