import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

// 解决ElementUI导航栏中的vue-router在3.0版本以上重复点菜单报错问题
const originalPush = VueRouter.prototype.push
VueRouter.prototype.push = function push(location) {
  return originalPush.call(this, location).catch(err => err)
}

const routes = [
  {
    path: '/',
    redirect: '/login'
  },
  {
    path: '/login',
    name: 'login',
    meta: {index: 0},
    component: () => import(/* webpackChunkName: "login" */ '../views/login/Login.vue')
  },
  {
    path: '/home',
    name: 'home',
    meta: {index: 1},
    redirect: '/dashboardover',
    component: () => import(/* webpackChunkName: "home" */ '../views/home/Home.vue'),
    children: [
      {
        path: '/dashboardover',
        name: 'dashboardover',
        meta: {index: 2},
        component: () => import(/* webpackChunkName: "dashboardover" */ '../views/dashboardover/Dashboardover.vue'),
      },
      {
        path: '/goodslist',
        name: 'goodslist',
        meta: {index: 3},
        component: () => import(/* webpackChunkName: "goods" */ '../views/goods/GoodsList.vue'),
      },
      {
        path: '/orderlist',
        name: 'orderlist',
        meta: {index: 4},
        component: () => import(/* webpackChunkName: "order" */ '../views/order/OrderList.vue'),
      },
      {
        path: '/customerlist',
        name: 'customerlist',
        meta: {index: 5},
        component: () => import(/* webpackChunkName: "customer" */ '../views/customer/CustomerList.vue'),
      },
    ]
  }
]

const router = new VueRouter({
  routes
})

export default router
