// 根据后端返回权限匹配对应路由
function routerMatch(permission, asyncRoute) {
  return new Promise((resolve, reject) => {
    const routers = [
      {
        path: '/home',
        name: 'home',
        meta: { index: 1 },
        // redirect: '/dashboardover',
        component: () => import(/* webpackChunkName: "home" */ '../views/home/Home.vue'),
        children: []
      }
    ]
    function createRouter(permission) {
      permission.forEach(item => {
        console.log('后台返回', item)
        if (item.childs && item.childs.length > 0) {
          createRouter(item.childs)
        }
        let path = item.path
        asyncRoute.find(s => {
          if (s.children) {
            s.children.find(y => {
              console.log('前端保存', y)
              console.log(path)
              if (y.name === path) {
                if (item.component === 'Layout') {
                  y.component = loadView()
                } else {
                  y.component = loadView(item.component)
                  routers[0].children.push(y)
                }
              }
            })
          } else {
            if (s.name === path) {
              s.component = loadView(item.component)
              routers[0].children.push(s)
            }
          }
        })
      })
    }
    createRouter(permission)
    console.log('最终的路由数组', routers)

    resolve(routers)
  })
}

let isDisposeRouter = 0
const disposeRouter = (perss) => {
  const routerList = []
  const loadView = (view) => {
    // return () => Promise.resolve(require(`@/views/${view}`).default)
    return () => import(`../views/${view}.vue`)
  }
  perss.forEach(menu => {
    if (menu.childs) {
      menu.childs.forEach(item => {
        let rList = {
          path: item.path,
          name: item.path.split('/')[1],
          meta: { id: item.id },
          // component: item.component
          component: () => require(`@/views/${item.component}.vue`).default
        }
        routerList.push(rList)
      })
    }
  })
  console.log('打印下处理好的路由数组', routerList)
  isDisposeRouter = 1
  return routerList
}

// 挂载路由守卫
router.beforeEach((to, from, next) => {
  // 获取token
  let token = window.sessionStorage.getItem('token')
  const tokenStart = window.sessionStorage.getItem('tokenStart')
  const nowDate = new Date().getTime()
  const timeOver = 3600 * 24 * 1000
  if (nowDate - tokenStart > timeOver) { // 判断token是否过期
    token = null
  }
  if (token) { // 判断token是否存在
    if (to.path === '/login') {
      next('/home')
    } else {
      console.log('现在的vuex路由列表', store.state.menuRouterList)
      if (store.state.menuRouterList.length === 0) {
        try {
          store.commit('getMenuRouterList')
          // if (router.getRoutes().length === 3) {
          console.log('路由添加前', router.getRoutes())
          const permiss = _.cloneDeep(store.state.menuRouterList)
          console.log('permiss', permiss)
          const routerLists = disposeRouter(permiss)
          console.log('处理函数返回的路由数组', routerLists)

          routerLists.forEach((item) => {
            router.addRoute('home', item)
          })
          // asyncRouter.forEach((item) => {
